import React from 'react';
import { Link } from 'react-router-dom';
import ContentLoader, { Facebook,List  } from 'react-content-loader'
const Horoscopetop=(props)=>{
	 let data=props.data.horoscope;
	
	return (
		
			<div className="horo-d-list">
				{ data.length===0 &&
				 <ul>
					<center><img src="data:image/gif;base64," /></center>
				</ul>
				}
				{
				data.length > 1 && 
				<ul>
				 {data.map((rowData, index) => (
					<li   key={index+1}>
						<Link to={'/horoscope/'+rowData.sunsign_en.toLowerCase()} className={index===0 ? 'active':''}>
							<img  key={index+1} src ={ require(`./../../assets/horoscope/${rowData.mphoto}`) } alt={rowData.sunsign_en}/>
							<p className="selected">{rowData.sunsign_en}</p>
						</Link>
					</li>
					))}
				</ul>
				}
			</div>

		)
};
export default Horoscopetop;